trigger.body:
v_n NUMBER;
v_amt NUMBER;
v_DateNull DATE := TO_DATE('01-01-1900','DD-MM-YYYY');
v_realtotalamount NUMBER;
BEGIN
  IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF;

  IF UPDATING THEN
    IF(:OLD.Processed='Y' AND :OLD.Docaction <> 'CL'
          AND(
               (COALESCE(:OLD.DOCUMENTNO, '.') <> COALESCE(:NEW.DOCUMENTNO, '.'))
               OR(COALESCE(:OLD.C_DOCTYPE_ID, '0') <> COALESCE(:NEW.C_DOCTYPE_ID, '0'))
               OR(COALESCE(:OLD.C_DOCTYPETARGET_ID, '0') <> COALESCE(:NEW.C_DOCTYPETARGET_ID, '0'))
               OR(COALESCE(:OLD.DATEGEN, v_DateNull) <> COALESCE(:NEW.DATEGEN, v_DateNull))
               OR(COALESCE(:old.AD_ORG_ID, '0') <> COALESCE(:new.AD_ORG_ID, '0'))
               OR(COALESCE(:old.AD_CLIENT_ID, '0') <> COALESCE(:new.AD_CLIENT_ID, '0'))
	       OR(COALESCE(:old.C_BPartner_ID, '0') <> COALESCE(:new.C_BPartner_ID, '0'))
	       OR(COALESCE(:old.C_Currency_ID, '0') <> COALESCE(:new.C_Currency_ID, '0'))
	       OR(COALESCE(:old.totalamount, '0') <> COALESCE(:new.totalamount, '0'))
	       OR(COALESCE(:old.withholdingnumber, '0') <> COALESCE(:new.withholdingnumber, '0'))
          )
    )THEN
      RAISE_APPLICATION_ERROR(-20000, '@20501@') ;
    END IF;

    IF (COALESCE(:OLD.C_BPartner_ID, '0')!=COALESCE(:NEW.C_BPartner_ID, '0') OR COALESCE(:OLD.C_Currency_ID, '0')!=COALESCE(:NEW.C_Currency_ID, '0'))  THEN
      SELECT COUNT(*)
        INTO v_n
        FROM SCO_Pwithho_Rec_Line
       WHERE SCO_Pwithholding_Receipt_ID = :NEW.SCO_Pwithholding_Receipt_ID;

       IF v_n>0 THEN
         RAISE_APPLICATION_ERROR(-20000, '@SCO_NoBPORCurr@') ;
       END IF;
     END IF;

    IF ((COALESCE(:OLD.DATEACCT,  TO_DATE('31-12-9999', 'DD-MM-YYYY')) <> COALESCE(:NEW.DATEACCT, TO_DATE('31-12-9999', 'DD-MM-YYYY'))) AND :new.DOCSTATUS NOT IN ('RE')) THEN
		IF (:old.POSTED = 'Y') THEN
			RAISE_APPLICATION_ERROR(-20000, '@20501@') ;
                END IF;
    END IF;
  END IF;

  IF(DELETING) THEN
    IF :OLD.Processed='Y' THEN
      RAISE_APPLICATION_ERROR(-20000, '@20501@') ;
    END IF;

    IF(:OLD.iswnumberautogen='Y') THEN
      RAISE_APPLICATION_ERROR(-20000, '@SCO_PWithholdingNumberAutoGenerated@') ;
    END IF;

    IF(:OLD.docstatus='VO') THEN
      RAISE_APPLICATION_ERROR(-20000, '@SCO_PWithholdingRecVoided@') ;
    END IF;

  END IF;
  IF(INSERTING) THEN
    IF :NEW.Processed='Y' THEN
      RAISE_APPLICATION_ERROR(-20000, '@20501@') ;
    END IF;
  END IF;

  --Openbravo UI bug
  IF(UPDATING) THEN
    IF(:OLD.totalamount<>:NEW.totalamount) THEN
      SELECT sum(convertedamount) INTO v_realtotalamount FROM sco_pwithho_rec_line WHERE sco_pwithholding_receipt_id = :NEW.sco_pwithholding_receipt_id;
      IF(v_realtotalamount<>:NEW.totalamount) THEN
        :NEW.totalamount := v_realtotalamount;
      END IF;
    END IF;
  END IF;

END SCO_PWITHHOL_REC_CHK_RES_TRG
