   TYPE RECORD IS REF CURSOR;
   Cur_Offer RECORD;
   v_ID VARCHAR2(32);
   v_currency_id VARCHAR2(32);
   v_pricelist_id VARCHAR2(32);
   v_Line NUMBER;

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

IF (DELETING) THEN
  v_ID:=:OLD.spr_budgetitem_id;
ELSE
  v_ID:=:NEW.spr_budgetitem_id;
END IF;


IF (INSERTING OR UPDATING) THEN
  IF(:NEW.QTYORDERED < 1)THEN
   RAISE_APPLICATION_ERROR(-20000,'@spr_budgetqty_morethanzero@');
  END IF;
END IF;



END SPR_BUDGETITEMLINE_VAL_TRG
