  v_AttrSetValueType M_Product.AttrSetValueType%TYPE;
  v_productname m_product.name%TYPE;
  v_isgeneric   CHAR(1);
  v_qtyonhand_sd NUMBER;
  v_em_swa_is_empty CHAR(1);

BEGIN
  IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF;
  IF (UPDATING) THEN

     SELECT em_swa_is_empty 
       INTO v_em_swa_is_empty
       FROM M_locator 
      WHERE m_locator_id = :NEW.m_locator_id;



     IF (:NEW.qtyonhand <> :OLD.qtyonhand) THEN

     IF(:NEW.qtyonhand = 0)THEN --El trigger es solo cuando algÃºn storage_detail llega a cero
       SELECT sum(qtyonhand) 
         INTO v_qtyonhand_sd 
         FROM m_storage_detail
        WHERE m_locator_id = :NEW.m_locator_id;

         IF(v_qtyonhand_sd IS NULL OR v_qtyonhand_sd =0)THEN

          UPDATE m_locator
             SET em_swa_is_empty='Y'
           WHERE m_locator_id = :NEW.m_locator_id;
         END IF;
     ELSIF(v_em_swa_is_empty='Y')THEN
          UPDATE m_locator
             SET em_swa_is_empty='N'
           WHERE m_locator_id = :NEW.m_locator_id;
     END IF;
    END IF;
  END IF;
END SWA_STORAGE_QTYONHAND_TRG
